import React, { useState, Component } from 'react';
import { Text, TextInput, View, Platform } from 'react-native';

import { styles } from '../Styles';
import *  as LoacalStore from '../../helper/CommonUtil';
import *  as Constants from '../constant';
import AsyncStorage from '@react-native-async-storage/async-storage';
export function SplashPage({ navigation }) {


    setTimeout(() => navigation.navigate('login'), 3000);

    let validateUserState = async function () {
        //check for users data in 
        var userinfo;
        if (!Platform.OS === 'web') {

            try {
                userinfo = await LoacalStore.getValueFromStore(Constants.USER_DATA);
            } catch (e) {
                console.log(e);
            }
        } else {
            try {
                userinfo = await AsyncStorage.getItem(Constants.USER_DATA)
            } catch (e) {
                console.log(e);
            }
            setTimeout(() => navigation.navigate('dashBoard'), 3000);

        }
        console.log(userinfo);
        console.log(Boolean(userinfo));
        if (userinfo && Boolean(userinfo)) {
            setTimeout(() => navigation.navigate('dashBoard'), 3000);
        } else {
            console.log('userinfo');
            setTimeout(() => navigation.navigate('login'), 1000);
        }
    }
    validateUserState();
    return (<View style={styles.center}>
        <Text>Logo Splash Screen</Text>
    </View>);
}
